mapper.select:
SELECT operator_worker_history.id, factory_house.factory_name, frame_setting.detailed, worker_detailed.worker_id, worker.miner, worker.workerName, group_setting.group_name, DATE_FORMAT( operator_worker_history.move_out_time, '%Y-%m-%d %H:%i:%s' )AS moveOutTime, DATE_FORMAT( operator_worker_history.come_in_time, '%Y-%m-%d %H:%i:%s' )AS comeInTime, operator_worker_history.reason, operator_worker_history.operator_id FROM operator_worker_history, worker_detailed, worker, factory_house, frame_setting, group_setting
operator_worker_history.worker_id = worker_detailed.worker_id AND worker_detailed.worker_id = worker.id AND worker_detailed.factory_id = factory_house.id AND worker_detailed.frame_id = frame_setting.id AND worker_detailed.group_id = group_setting.id
AND operator_worker_history.move_out_time > #{startTime}
AND operator_worker_history.move_out_time < #{startTime}
AND operator_worker_history.reason like #{keyword}

